sites.placeBetScript:
var input = document.querySelectorAll("app-bet-slip input")[2];
var inputOdds = document.querySelectorAll("app-bet-slip input")[0];
var nativeInputValueSetter = Object.getOwnPropertyDescriptor(window.HTMLInputElement.prototype, "value").set;
var ev1 = new Event('input', { bubbles: true});
var ev2 = new Event('change', { bubbles: true});
nativeInputValueSetter.call(input, '$stake');
input.dispatchEvent(ev1);
input.dispatchEvent(ev2);
if ($odds != 0.0) {
nativeInputValueSetter.call(inputOdds, '$odds');
inputOdds.dispatchEvent(ev1);
inputOdds.dispatchEvent(ev2);
}

